import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { authAPI } from '../services/api';

const Register = ({ onLogin }) => {
  const [formData, setFormData] = useState({
    name: '',
    registration_number: '',
    department: '',
    email: '',
    password: '',
    contact_number: ''
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const departments = [
    'Computer Science',
    'Information Technology',
    'Engineering',
    'Applied Science',
    'Communication and Business Studies',
    'Siddha Medicine'
  ];

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      const response = await authAPI.register(formData);
      // Show success message and redirect to login
      alert('Registration successful! Please login with your credentials.');
      window.location.href = '/login';
    } catch (error) {
      setError(error.response?.data?.error || 'Registration failed');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{width: '100vw', height: '100vh', position: 'relative', background: '#EBF5FD', backgroundImage: 'url("../../image/bg 1.png")', backgroundSize: 'cover', backgroundPosition: 'center', overflow: 'hidden'}}>
      <div style={{width: '87%', height: '85%', left: '6.5%', top: '7%', position: 'absolute', background: 'white', boxShadow: '0px 4px 4px 3px rgba(0, 0, 0, 0.25)', borderRadius: 20}} />
      <div style={{width: '15%', height: '25%', left: '3%', top: '2%', position: 'absolute', background: 'white', borderRadius: '50%'}} />
      <img style={{width: '18%', height: '27%', left: '2%', top: '0%', position: 'absolute'}} src="../../image/logo2 1.png" />

      {/* Left Side Content */}
      <div style={{width: '28%', left: '15%', top: '17%', position: 'absolute', color: '#03045E', fontSize: '3.5vw', fontFamily: 'Calibri', fontWeight: '700'}}>Welcome to </div>
      <div style={{width: '12%', left: '15%', top: '25%', position: 'absolute', color: 'black', fontSize: '3.5vw', fontFamily: 'Calibri', fontWeight: '700'}}>Back2U</div>
      <div style={{width: '23%', left: '15%', top: '35%', position: 'absolute', color: 'black', fontSize: '1.5vw', fontFamily: 'Calibri', fontWeight: '400'}}>Your trusted space to recover and return rightful owners</div>
      <div style={{width: '15%', left: '15%', top: '47%', position: 'absolute', textAlign: 'center', color: 'black', fontSize: '1.5vw', fontFamily: 'Calibri', fontWeight: '700'}}>Create your account</div>

      {/* Form */}
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="name"
          value={formData.name}
          onChange={handleChange}
          placeholder="Enter your full name"
          required
          style={{
            width: '30%', height: '5%', left: '60%', top: '15%', position: 'absolute',
            padding: '10px', fontSize: '1.2vw', border: '1px solid #ccc', borderRadius: 5,
            background: 'rgba(235, 245, 253, 0.50)'
          }}
        />

        <input
          type="text"
          name="registration_number"
          value={formData.registration_number}
          onChange={handleChange}
          placeholder="Enter your Registration number (Username)"
          required
          style={{
            width: '30%', height: '5%', left: '60%', top: '22%', position: 'absolute',
            padding: '10px', fontSize: '1.2vw', border: '1px solid #ccc', borderRadius: 5,
            background: 'rgba(235, 245, 253, 0.50)'
          }}
        />

        <select
          name="department"
          value={formData.department}
          onChange={handleChange}
          required
          style={{
            width: '30%', height: '5%', left: '60%', top: '29%', position: 'absolute',
            padding: '10px', fontSize: '1.2vw', border: '1px solid #ccc', borderRadius: 5,
            background: 'rgba(235, 245, 253, 0.50)'
          }}
        >
          <option value="">Select Department</option>
          {departments.map(dept => (
            <option key={dept} value={dept}>{dept}</option>
          ))}
        </select>

        <input
          type="email"
          name="email"
          value={formData.email}
          onChange={handleChange}
          placeholder="Enter your email"
          required
          style={{
            width: '30%', height: '5%', left: '60%', top: '36%', position: 'absolute',
            padding: '10px', fontSize: '1.2vw', border: '1px solid #ccc', borderRadius: 5,
            background: 'rgba(235, 245, 253, 0.50)'
          }}
        />

        <input
          type="password"
          name="password"
          value={formData.password}
          onChange={handleChange}
          placeholder="Create a Strong password"
          required
          style={{
            width: '30%', height: '5%', left: '60%', top: '43%', position: 'absolute',
            padding: '10px', fontSize: '1.2vw', border: '1px solid #ccc', borderRadius: 5,
            background: 'rgba(235, 245, 253, 0.50)'
          }}
        />

        <input
          type="tel"
          name="contact_number"
          value={formData.contact_number}
          onChange={handleChange}
          placeholder="Enter your contact number"
          required
          style={{
            width: '30%', height: '5%', left: '60%', top: '50%', position: 'absolute',
            padding: '10px', fontSize: '1.2vw', border: '1px solid #ccc', borderRadius: 5,
            background: 'rgba(235, 245, 253, 0.50)'
          }}
        />

        {/* Error Message */}
        {error && (
          <div style={{
            left: '60%', top: '58%', position: 'absolute',
            color: 'red', fontSize: '1vw', fontFamily: 'Calibri'
          }}>
            {error}
          </div>
        )}

        {/* Submit Button */}
        <button
          type="submit"
          disabled={loading}
          style={{
            width: '32%', height: '8%', left: '59%', top: '62%', position: 'absolute',
            background: '#2E72F9', borderRadius: 10, border: 'none',
            color: 'white', fontSize: '1.8vw', fontFamily: 'Calibri', fontWeight: '400',
            cursor: loading ? 'not-allowed' : 'pointer'
          }}
        >
          {loading ? 'Signing Up...' : 'Sign Up'}
        </button>
      </form>

      {/* Sign In Link */}
      <div style={{left: '60%', top: '73%', position: 'absolute', color: 'black', fontSize: '1.5vw', fontFamily: 'Calibri', fontWeight: '400'}}>Already have an account? 
        <Link to="/login" style={{color: '#2E72F9', textDecoration: 'none', marginLeft: 10}}>Sign In</Link>
      </div>
    </div>
  );
};

export default Register;
