import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const Profile = ({ user, token, onLogout }) => {
  const navigate = useNavigate();
  const [profileData, setProfileData] = useState(null);
  const [editMode, setEditMode] = useState(false);
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [message, setMessage] = useState('');
  const [showPasswordChange, setShowPasswordChange] = useState(false);
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [showLogoutModal, setShowLogoutModal] = useState(false);

  const [formData, setFormData] = useState({
    name: '',
    email: '',
    contact_number: '',
    department: ''
  });

  const [passwordData, setPasswordData] = useState({
    current_password: '',
    new_password: '',
    confirm_password: ''
  });

  const [errors, setErrors] = useState({});

  useEffect(() => {
    loadProfile();
  }, []);

  const loadProfile = async () => {
    try {
      console.log('Loading profile with token:', token);
      const response = await fetch('/api/profile', {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      console.log('Profile response status:', response.status);

      if (!response.ok) {
        const errorText = await response.text();
        console.error('Profile API error:', errorText);
        throw new Error(`Failed to load profile: ${response.status}`);
      }

      const result = await response.json();
      console.log('Profile data received:', result);
      const data = result.data;

      setProfileData(data);
      setFormData({
        name: data.name,
        email: data.email,
        contact_number: data.contact_number,
        department: data.department
      });
      setLoading(false);
    } catch (error) {
      console.error('Failed to load profile:', error);
      setMessage(`Failed to load profile data: ${error.message}`);
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
    if (errors[name]) {
      setErrors(prev => ({ ...prev, [name]: '' }));
    }
  };

  const validateForm = () => {
    const newErrors = {};

    if (!formData.name || formData.name.length < 3) {
      newErrors.name = 'Name must be at least 3 characters';
    }

    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!formData.email || !emailRegex.test(formData.email)) {
      newErrors.email = 'Please enter a valid email address';
    }

    const phoneRegex = /^\+94\s?\d{2}\s?\d{3}\s?\d{4}$/;
    if (!formData.contact_number || !phoneRegex.test(formData.contact_number)) {
      newErrors.contact_number = 'Please enter a valid phone number (+94 XX XXX XXXX)';
    }

    if (!formData.department) {
      newErrors.department = 'Please select a department';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSave = async () => {
    if (!validateForm()) return;

    setSaving(true);
    try {
      const response = await fetch('/api/profile', {
        method: 'PUT',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(formData)
      });

      const result = await response.json();

      if (!response.ok) {
        throw new Error(result.error || 'Failed to update profile');
      }

      setProfileData(prev => ({ ...prev, ...formData }));
      setEditMode(false);
      setMessage('Profile updated successfully!');
      setTimeout(() => setMessage(''), 3000);
    } catch (error) {
      setMessage(error.message || 'Failed to update profile');
    } finally {
      setSaving(false);
    }
  };

  const handleCancel = () => {
    setFormData({
      name: profileData.name,
      email: profileData.email,
      contact_number: profileData.contact_number,
      department: profileData.department
    });
    setEditMode(false);
    setErrors({});
  };

  const getContributionBadge = (score) => {
    if (score >= 90) return { emoji: 'ðŸŒŸ', text: 'Super Helper' };
    if (score >= 70) return { emoji: 'â­', text: 'Active Member' };
    if (score >= 50) return { emoji: 'ðŸ‘', text: 'Good Member' };
    return { emoji: 'ðŸ†•', text: 'New Member' };
  };

  const styles = {
    container: {
      minHeight: '100vh',
      background: '#EFF6FF',
      padding: '20px',
      fontFamily: 'Calibri, sans-serif'
    },
    content: {
      maxWidth: '1000px',
      margin: '0 auto'
    },
    header: {
      marginBottom: '30px'
    },
    backButton: {
      background: 'none',
      border: 'none',
      color: '#3B82F6',
      fontSize: '16px',
      cursor: 'pointer',
      marginBottom: '20px',
      display: 'flex',
      alignItems: 'center',
      gap: '8px'
    },
    title: {
      fontSize: '32px',
      color: '#1F2937',
      margin: '0',
      fontWeight: 'bold'
    },
    section: {
      background: 'white',
      borderRadius: '16px',
      padding: '30px',
      marginBottom: '20px',
      boxShadow: '0 4px 6px rgba(0,0,0,0.1)'
    },
    sectionTitle: {
      fontSize: '20px',
      fontWeight: 'bold',
      color: '#1F2937',
      marginBottom: '20px',
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center'
    },
    profileHeader: {
      textAlign: 'center',
      marginBottom: '30px'
    },
    avatar: {
      width: '150px',
      height: '150px',
      borderRadius: '50%',
      background: '#3B82F6',
      color: 'white',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      fontSize: '48px',
      fontWeight: 'bold',
      margin: '0 auto 15px',
      cursor: 'pointer',
      position: 'relative'
    },
    avatarOverlay: {
      position: 'absolute',
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      background: 'rgba(0,0,0,0.7)',
      borderRadius: '50%',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      color: 'white',
      fontSize: '14px',
      opacity: 0,
      transition: 'opacity 0.3s'
    },
    userName: {
      fontSize: '28px',
      fontWeight: 'bold',
      color: '#1F2937',
      margin: '0 0 5px 0'
    },
    userReg: {
      fontSize: '18px',
      color: '#6B7280',
      margin: '0 0 5px 0'
    },
    userDept: {
      fontSize: '16px',
      color: '#6B7280',
      margin: '0 0 10px 0'
    },
    memberSince: {
      fontSize: '14px',
      color: '#6B7280',
      margin: '0'
    },
    fieldGroup: {
      marginBottom: '20px'
    },
